import type React from "react";
import { cn } from "@/registry/default/lib/utils";
import Heading from "@/registry/default/components/domain-ui-pro/heading";
import SubHeading from "@/registry/default/components/domain-ui-pro/sub-heading";

interface PageTitleProps extends React.HTMLAttributes<HTMLDivElement> {
  title: string;
  subtitle?: string;
  headingLevel?: 1 | 2 | 3 | 4 | 5 | 6;
  headingVariant?: "default" | "muted" | "gradient";
  subtitleVariant?: "default" | "muted" | "accent";
  alignment?: "left" | "center" | "right";
}

export default function PageTitle({
  title,
  subtitle,
  headingLevel = 1,
  headingVariant = "default",
  subtitleVariant = "default",
  alignment = "left",
  className,
  ...props
}: PageTitleProps) {
  const alignmentClasses = {
    left: "text-left",
    center: "text-center",
    right: "text-right",
  };

  return (
    <div
      className={cn("space-y-2", alignmentClasses[alignment], className)}
      {...props}
    >
      <Heading level={headingLevel} variant={headingVariant}>
        {title}
      </Heading>
      {subtitle && (
        <SubHeading variant={subtitleVariant} size="lg">
          {subtitle}
        </SubHeading>
      )}
    </div>
  );
}

import type React from "react";
import { cn } from "@/registry/default/lib/utils";

interface HeadingProps extends React.HTMLAttributes<HTMLHeadingElement> {
  level?: 1 | 2 | 3 | 4 | 5 | 6;
  variant?: "default" | "muted" | "gradient";
}

export default function Heading({
  level = 1,
  variant = "default",
  className,
  children,
  ...props
}: HeadingProps) {
  const Tag = `h${level}` as "h1" | "h2" | "h3" | "h4" | "h5" | "h6";

  const variants = {
    default: "text-foreground font-semibold tracking-tight",
    muted: "text-muted-foreground font-medium tracking-tight",
    gradient:
      "bg-gradient-to-r from-foreground to-muted-foreground bg-clip-text text-transparent font-bold tracking-tight",
  };

  const sizes = {
    1: "text-4xl lg:text-5xl",
    2: "text-3xl lg:text-4xl",
    3: "text-2xl lg:text-3xl",
    4: "text-xl lg:text-2xl",
    5: "text-lg lg:text-xl",
    6: "text-base lg:text-lg",
  };

  return (
    <Tag className={cn(variants[variant], sizes[level], className)} {...props}>
      {children}
    </Tag>
  );
}

import type React from "react";
import { cn } from "@/registry/default/lib/utils";

interface SubHeadingProps extends React.HTMLAttributes<HTMLParagraphElement> {
  variant?: "default" | "muted" | "accent";
  size?: "sm" | "md" | "lg";
}

export default function SubHeading({
  variant = "default",
  size = "md",
  className,
  children,
  ...props
}: SubHeadingProps) {
  const variants = {
    default: "text-muted-foreground",
    muted: "text-muted-foreground/70",
    accent: "text-primary",
  };

  const sizes = {
    sm: "text-sm",
    md: "text-base lg:text-lg",
    lg: "text-lg lg:text-xl",
  };

  return (
    <p
      className={cn(
        "font-medium leading-relaxed",
        variants[variant],
        sizes[size],
        className
      )}
      {...props}
    >
      {children}
    </p>
  );
}
