"use client";

import { useId } from "react";
import type * as React from "react";
import { RegexInput } from "@/registry/default/components/domain-ui/regex-input";
import { Label } from "@/registry/default/components/ui/label";

const INDIAN_PASSPORT_REGEX = /^[A-Z][0-9]{7}$/;

export function IndianPassport(props: React.ComponentProps<"input">) {
  const generatedId = useId();
  const id = props.id || generatedId;

  return (
    <div className="*:not-first:mt-2">
      <Label htmlFor={id}>Indian Passport</Label>
      <RegexInput regex={INDIAN_PASSPORT_REGEX} {...props} id={id} />
    </div>
  );
}
